mapper.select:
select
c.id, u.name as owner, c.source, cus.name as customerId, c.fullname, c.appellation, c.email, c.mphone, c.job, c.birth, c.createBy, c.createTime, c.editBy, c.editTime, c.description, c.contactSummary, c.nextContactTime, c.address
from tbl_contacts c join tbl_user u on c.owner = u.id join tbl_customer cus on c.customerId = cus.id where c.id = #{id}
